    try {
        if (isDebugEnabled()) {
            debugCode("setByte("+parameterIndex+", "+x+");");
        }
        setParameter(parameterIndex, ValueByte.get(x));
    } catch (Exception e) {
        throw logAndConvert(e);
    }
}

