        int id = getNextId(TraceObject.ARRAY);
        if (isDebugEnabled()) {
            debugCodeAssign("Array", TraceObject.ARRAY, id, "getArray(" + columnIndex + ")");
        }
        Value v = get(columnIndex);
        return v == ValueNull.INSTANCE ? null : new JdbcArray(conn, v, id);
    } catch (Exception e) {
        throw logAndConvert(e);
    }
}

