public void setTop(int val)
{
  try {
    m_map[m_firstFree - 1] = val;
  }
  catch (ArrayIndexOutOfBoundsException e)
  {
    throw new EmptyStackException();
  }
}

public void setTop(Object val)
{
  try {
    m_map[m_firstFree - 1] = val;
  }
  catch (ArrayIndexOutOfBoundsException e)
  {
    throw new EmptyStackException();
  }
}

public int peek(int n)
{
  try {
    return m_map[m_firstFree-(1+n)];
  }
  catch (ArrayIndexOutOfBoundsException e)
  {
    throw new EmptyStackException();
  }
}

public Object peek(int n)
{
  try {
    return m_map[m_firstFree-(1+n)];
  }
  catch (ArrayIndexOutOfBoundsException e)
  {
    throw new EmptyStackException();
  }
}

public int search(int o)
{

  int i = lastIndexOf(o);

  if (i >= 0)
  {
    return size() - i;
  }

  return -1;
}

public int search(Object o)
{

  int i = lastIndexOf(o);

  if (i >= 0)
  {
    return size() - i;
  }

  return -1;
}

public void addUniqueAttribute(String rawName, String value, int flags)
    throws SAXException
{
    if (m_firstTagNotEmitted)
    {
        flush();
    }
    m_handler.addUniqueAttribute(rawName, value, flags);

}

public void externalEntityDecl(
    String name,
    String publicId,
    String systemId)
    throws SAXException
{
    if (m_firstTagNotEmitted)
    {
        flush();
    }
    m_handler.externalEntityDecl(name, publicId, systemId);
}

public int distanceTo(Type type) {
    if (type == this) {
        return 0;
    }
    else if (type == Type.Real) {
        return 1;
    }
    else
        return Integer.MAX_VALUE;
}

public int distanceTo(Type type) {
    if (type == this) {
        return 0;
    }
    else if (type == Type.Int) {
        return 1;
    }
    else {
        return Integer.MAX_VALUE;
    }
}

public final boolean contains(int s)
{

  for (int i = 0; i < m_firstFree; i++)
  {
    if (m_map[i] == s)
      return true;
  }

  return false;
}

