public final int indexOf(int elem)
{

  for (int i = 0; i < m_firstFree; i++)
  {
    if (m_map[i] == elem)
      return i;
  }

  return java.lang.Integer.MIN_VALUE;
}

public final int indexOf(Object elem)
{

  for (int i = 0; i < m_firstFree; i++)
