    if (m_map[i] == elem)
      return i;
  }

  return java.lang.Integer.MIN_VALUE;
}

public final int lastIndexOf(int elem)
{

  for (int i = (m_firstFree - 1); i >= 0; i--)
  {
    if (m_map[i] == elem)
      return i;
  }

  return java.lang.Integer.MIN_VALUE;
}

