void deletedText(NodeImpl node, int offset, int count) {
    // notify ranges
    if (ranges != null) {
        int size = ranges.size();
        for (int i = 0; i != size; i++) {
            ((RangeImpl)ranges.elementAt(i)).receiveDeletedText(node,
                                                            offset, count);
        }
    }
}

void splitData(Node node, Node newNode, int offset) {
    // notify ranges
    if (ranges != null) {
        int size = ranges.size();
        for (int i = 0; i != size; i++) {
            ((RangeImpl)ranges.elementAt(i)).receiveSplitData(node,
                                                          newNode, offset);
        }
    }
}

void insertedText(NodeImpl node, int offset, int count) {
    // notify ranges
    if (ranges != null) {
        int size = ranges.size();
        for (int i = 0; i != size; i++) {
            ((RangeImpl)ranges.elementAt(i)).receiveInsertedText(node,
                                                            offset, count);
        }
    }
}

void splitData(Node node, Node newNode, int offset) {
    // notify ranges
    if (ranges != null) {
        int size = ranges.size();
        for (int i = 0; i != size; i++) {
            ((RangeImpl)ranges.elementAt(i)).receiveSplitData(node,
                                                          newNode, offset);
        }
    }
}

public void clear() {
   this.name         = null;
   this.publicId     = null;
   this.systemId     = null;
   this.baseSystemId = null;
   this.notation     = null;
   this.value        = null;
   this.isPE         = false;
   this.inExternal   = false;

} // clear

public void clear() {
    this.type              = -1;
    this.name              = null;
    this.enumeration       = null;
    this.list              = false;
    this.defaultType       = -1;
    this.defaultValue      = null;
    this.nonNormalizedDefaultValue      = null;
    this.datatypeValidator = null;
} // clear

public int push(int i)
{

  if ((m_firstFree + 1) >= m_mapSize)
  {
    m_mapSize += m_blocksize;

    int newMap[] = new int[m_mapSize];

    System.arraycopy(m_map, 0, newMap, 0, m_firstFree + 1);

    m_map = newMap;
  }

  m_map[m_firstFree] = i;

  m_firstFree++;

  return i;
}

