public EqualsBuilder append(Object[] lhs, Object[] rhs) {
    if (!isEquals) {
        return this;
    }
    if (lhs == rhs) {
        return this;
    }
    if (lhs == null || rhs == null) {
        this.setEquals(false);
        return this;
    }
    if (lhs.length != rhs.length) {
        this.setEquals(false);
        return this;
    }
    for (int i = 0; i < lhs.length && isEquals; ++i) {
        append(lhs[i], rhs[i]);
    }
    return this;
}

